import React, { Component } from 'react';
import NavBar from "./components/NavBar";
import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";
import MemoryCard from "./components/MemoryCard";
import friends from "./friends.json";


import './App.css';

class App extends Component {

 // Setting this.state.friends to the friends json array
 state = {
    friends,
    score: 0,
    topScore: 0,
    message: 'Click an image to begin!',
    clickedList: []
  };

  // 1) When card clicked check if unique id is in selectedCards 
  // - if in array then game over
  //    - check if current score is greater than topScore
  //         - if greater save score as current topScore
  //    - display game over to screen
  //    - startNewGame()
  //       - reset score to zero
  //       - shuffle cards - random array
  //       - render to page
  // 2) if not in array
  //    - Check if wonGame()
  //    - increase score counter by 1
  //    - add id to selectedCards
  //    - shuffle cards - random array
  //    - render to page

  // When card is clicked onClick="shuffleCards" 
  shuffleCards = (array) => {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    this.setState({
      friends: array
    })  
  }


  wonGame() {

  }


  startNewGame() {
    const message = 'Click an image to begin!'
    this.setState({
      clickedList: [],
      score: 0,
      message: message
    })
  }

  handleClick = (id) => {
    // const clickedList = this.state.clickedList;
    // const score = this.state.score;

    const { clickedList, score, message, friends, topScore } = this.state;
    shuffleCards(friends);
    // this.setState({
    //   friends: this.shuffleCards(friends)
    // })

    let correctGuessMessage = this.state.message;
    correctGuessMessage = 'You guessed correctly!';
    let wrongGuessMessage = this.state.message;
    wrongGuessMessage = 'You guessed incorrectly!';
    // Can use indexOf()
    // Returns true or false if included
    const isClicked = clickedList.includes(id);

    if (isClicked) {
      const bestScore = Math.max(score, topScore)

      this.resetGame(bestScore)

      this.setState({
        message: wrongGuessMessage
      })
      this.startNewGame()
    } else {
      const newScore = score + 1;

      this.setState({
        clickedList: [...clickedList, id],
        score: newScore,
        message: correctGuessMessage
      })
      // Move this to a Top Score function can be used even if you loose
      if(newScore === friends.length){
        this.resetGame(newScore)
        // this.setState({
        //   clickedList: [...clickedList, id],
        //   topScore: newScore,
        //   message: 'You win'
        // })
      }
      // this.startNewGame()
    }
  }

  resetGame = (highscore) => {
    this.setState({
      score: 0,
      clickedList: [],
      topScore: highscore
    })
  }



  render() {
    return (
      <div className="App">
          <NavBar score={this.state.score} message={this.state.message}/>
          <Header />
          <Main>
            {this.state.friends.map(friend => (
              <MemoryCard
                id={friend.id}
                key={friend.id}
                name={friend.name}
                image={friend.image}
                handleClick={this.handleClick}
              />
            ))}
          </Main>
          <Footer />
      </div>
    );
  }
}

export default App;
